function customMap(array, callback) {
    let result = [];

    for (let i = 0; i < array.length; i++) {
        let newValue = callback(array[i], i, array); 
        result.push(newValue);
    }

    return result;
}


let numbers = [1, 2, 3];
let doubled = customMap(numbers, function(num) {
    return num * 2;
});

console.log(doubled); 